import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { useCart } from "../context/CartContext";
import '../../style/cart.css'

// Thêm BASE_URL
const BASE_URL = 'http://localhost:8080';

const CartPage = () => {
    const { cart, dispatch } = useCart();
    const [message, setMessage] = useState(null);
    const [userAddress, setUserAddress] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('CASH'); // Mặc định là tiền mặt
    const [showCheckoutForm, setShowCheckoutForm] = useState(false);
    const navigate = useNavigate();

    // Kiểm tra đăng nhập khi trang được tải
    useEffect(() => {
        if (!ApiService.isAuthenticated()) {
            // Hiện thông báo yêu cầu đăng nhập
            if (window.confirm("Bạn cần đăng nhập để xem giỏ hàng. Đăng nhập ngay?")) {
                navigate("/login");
            } else {
                navigate("/"); // Chuyển hướng về dashboard nếu không đồng ý
            }
        } else {
            // Nếu đã đăng nhập, tiếp tục tải thông tin địa chỉ
            fetchUserAddress();
        }
    }, [navigate]);

    // Tách hàm lấy địa chỉ riêng để xử lý
    const fetchUserAddress = async () => {
        try {
            const userInfo = await ApiService.getLoggedInUserInfo();
            if (userInfo && userInfo.address) {
                setUserAddress(userInfo.address);
            }
        } catch (error) {
            console.error("Error fetching user info:", error);
        }
    };

    const incrementItem = (product) => {
        dispatch({ type: 'INCREMENT_ITEM', payload: product });
    }

    const decrementItem = (product) => {
        const cartItem = cart.find(item => item.id === product.id);
        if (cartItem && cartItem.quantity > 1) {
            dispatch({ type: 'DECREMENT_ITEM', payload: product });
        } else {
            dispatch({ type: 'REMOVE_ITEM', payload: product });
        }
    }

    const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

    const initiateCheckout = () => {
        // Không cần kiểm tra đăng nhập nữa vì đã kiểm tra khi trang tải
        setShowCheckoutForm(true);
    }

    const handleAddressChange = (e) => {
        const { name, value } = e.target;
        setUserAddress(prev => ({
            ...prev,
            [name]: value
        }));
    }

    const handleCheckout = async () => {
        if (!userAddress || !userAddress.street || !userAddress.city) {
            setMessage("Vui lòng nhập đầy đủ thông tin địa chỉ");
            return;
        }

        const orderItems = cart.map(item => ({
            productId: item.id,
            quantity: item.quantity
        }));

        const orderRequest = {
            totalPrice,
            items: orderItems,
            paymentMethod,
            shippingAddress: `${userAddress.street}, ${userAddress.city}, ${userAddress.state || ''}, ${userAddress.country || ''}`,
            status: paymentMethod === 'CASH' ? 'PENDING' : 'PROCESSING'
        }

        try {
            // Lưu địa chỉ người dùng
            if (userAddress) {
                // Đảm bảo gửi đúng định dạng địa chỉ
                const addressToSave = {
                    id: userAddress.id, // Có thể null nếu là địa chỉ mới
                    street: userAddress.street,
                    city: userAddress.city,
                    state: userAddress.state || '',
                    country: userAddress.country || ''
                };
                
                await ApiService.saveAddress(addressToSave);
            }
            
            // Tạo đơn hàng
            const response = await ApiService.createOrder(orderRequest);
            
            if (response.orderId || response.id) {
                const orderId = response.orderId || response.id;
                
                if (paymentMethod === 'CASH') {
                    setMessage('Đặt hàng thành công!');
                    dispatch({ type: 'CLEAR_CART' });
                } else if (paymentMethod === 'VNPAY') {
                    // Chuyển hướng đến VNPAY
                    const paymentUrl = await ApiService.createVNPayUrl(orderId, totalPrice);
                    window.location.href = paymentUrl;
                } else if (paymentMethod === 'ZALOPAY') {
                    try {
                        // Gọi API ZaloPay và log kết quả
                        console.log("Gọi ZaloPay với amount:", totalPrice);
                        const zaloPayResponse = await ApiService.createZaloPayUrl(orderId, totalPrice);
                        console.log("ZaloPay response:", zaloPayResponse);
                        
                        // Xử lý response
                        if (zaloPayResponse && zaloPayResponse.zalopay_response) {
                            // Parse response nếu cần
                            let zaloPayData;
                            if (typeof zaloPayResponse.zalopay_response === 'string') {
                                try {
                                    zaloPayData = JSON.parse(zaloPayResponse.zalopay_response);
                                } catch (e) {
                                    console.error("Lỗi parse ZaloPay response:", e);
                                    setMessage("Lỗi khi xử lý phản hồi từ ZaloPay");
                                    return;
                                }
                            } else {
                                zaloPayData = zaloPayResponse.zalopay_response;
                            }
                            
                            // Kiểm tra kết quả và chuyển hướng
                            console.log("ZaloPay data:", zaloPayData);
                            if (zaloPayData.return_code === 1) {
                                // Lưu app_trans_id vào localStorage nếu cần theo dõi sau
                                if (zaloPayResponse.app_trans_id) {
                                    localStorage.setItem('zalopay_trans_id', zaloPayResponse.app_trans_id);
                                }
                                
                                // Chuyển hướng đến trang thanh toán ZaloPay
                                window.location.href = zaloPayData.order_url;
                            } else {
                                setMessage(`Lỗi ZaloPay: ${zaloPayData.return_message || 'Không thể tạo thanh toán'}`);
                            }
                        } else {
                            setMessage('Không nhận được phản hồi hợp lệ từ ZaloPay');
                        }
                    } catch (error) {
                        console.error("Error with ZaloPay:", error);
                        setMessage(`Lỗi khi kết nối ZaloPay: ${error.message || 'Lỗi không xác định'}`);
                    }
                }
                
                setShowCheckoutForm(false);
            } else {
                setMessage('Không thể tạo đơn hàng, vui lòng thử lại');
            }
        } catch (error) {
            console.error("Checkout error:", error);
            setMessage(error.response?.data?.message || error.message || 'Không thể tạo đơn hàng');
            setTimeout(() => {
                setMessage('');
            }, 5000);
        }
    };

    return (
        <div className="cart-page">
            <h1>Giỏ hàng</h1>
            {message && <p className="response-message">{message}</p>}

            {cart.length === 0 ? (
                <p>Giỏ hàng của bạn đang trống</p>
            ) : (
                <div>
                    {!showCheckoutForm ? (
                        <>
                            <ul>
                                {cart.map(item => (
                                    <li key={item.id}>
                                        <img 
                                            src={`${BASE_URL}${item.imageUrl}`}
                                            alt={item.name}
                                            onError={(e) => {
                                                e.target.src = '/images/placeholder.png';
                                            }} 
                                        />
                                        <div>
                                            <h2>{item.name}</h2>
                                            <p>{item.description}</p>
                                            <div className="quantity-controls">
                                                <button onClick={()=> decrementItem(item)}>-</button>
                                                <span>{item.quantity}</span>
                                                <button onClick={()=> incrementItem(item)}>+</button>
                                            </div>
                                            <span>${item.price.toFixed(2)}</span>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <h2>Tổng cộng: ${totalPrice.toFixed(2)}</h2>
                            <button className="checkout-button" onClick={initiateCheckout}>Thanh toán</button>
                        </>
                    ) : (
                        <div className="checkout-form">
                            <h2>Thông tin đặt hàng</h2>
                            
                            <div className="order-summary">
                                <h3>Tóm tắt đơn hàng</h3>
                                <ul>
                                    {cart.map(item => (
                                        <li key={item.id}>
                                            <span>{item.name} x {item.quantity}</span>
                                            <span>${(item.price * item.quantity).toFixed(2)}</span>
                                        </li>
                                    ))}
                                </ul>
                                <div className="total">
                                    <strong>Tổng cộng:</strong>
                                    <strong>${totalPrice.toFixed(2)}</strong>
                                </div>
                            </div>
                            
                            <div className="address-form">
                                <h3>Địa chỉ giao hàng</h3>
                                <div className="form-row">
                                    <input
                                        type="text"
                                        name="street"
                                        placeholder="Địa chỉ đường phố"
                                        value={userAddress?.street || ''}
                                        onChange={handleAddressChange}
                                    />
                                </div>
                                <div className="form-row">
                                    <input
                                        type="text"
                                        name="city"
                                        placeholder="Thành phố"
                                        value={userAddress?.city || ''}
                                        onChange={handleAddressChange}
                                    />
                                </div>
                                <div className="form-row">
                                    <input
                                        type="text"
                                        name="state"
                                        placeholder="Tỉnh/Thành phố"
                                        value={userAddress?.state || ''}
                                        onChange={handleAddressChange}
                                    />
                                </div>
                                <div className="form-row">
                                    <input
                                        type="text"
                                        name="country"
                                        placeholder="Quốc gia"
                                        value={userAddress?.country || ''}
                                        onChange={handleAddressChange}
                                    />
                                </div>
                            </div>
                            
                            <div className="payment-methods">
                                <h3>Phương thức thanh toán</h3>
                                <div className="payment-options">
                                    <label>
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="CASH"
                                            checked={paymentMethod === 'CASH'}
                                            onChange={() => setPaymentMethod('CASH')}
                                        />
                                        Thanh toán khi nhận hàng
                                    </label>
                                    <label>
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="VNPAY"
                                            checked={paymentMethod === 'VNPAY'}
                                            onChange={() => setPaymentMethod('VNPAY')}
                                        />
                                        Thanh toán qua VNPAY
                                    </label>
                                    <label>
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="ZALOPAY"
                                            checked={paymentMethod === 'ZALOPAY'}
                                            onChange={() => setPaymentMethod('ZALOPAY')}
                                        />
                                        Thanh toán qua ZaloPay
                                    </label>
                                </div>
                            </div>
                            
                            <div className="checkout-actions">
                                <button className="back-button" onClick={() => setShowCheckoutForm(false)}>Quay lại</button>
                                <button className="confirm-button" onClick={handleCheckout}>Xác nhận đặt hàng</button>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default CartPage;
