import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from "../../service/ApiService";
import '../../style/profile.css';
import Pagination from "../common/Pagination";

const ProfilePage = () => {
    const [userInfo, setUserInfo] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserInfo();
    }, []);

    const fetchUserInfo = async () => {
        try {
            // Sửa từ getLoginUser thành getLoggedInUserInfo
            const response = await ApiService.getLoggedInUserInfo();
            setUserInfo(response);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching user info:', error);
            setError(error.response?.data?.message || error.message || 'Unable to fetch user info');
            setLoading(false);
            
            // Nếu token hết hạn hoặc không hợp lệ, chuyển về trang login
            if (error.response?.status === 401 || error.response?.status === 403) {
                ApiService.logout(); // Xóa token và role
                navigate('/login');
            }
        }
    };

    const handleAddressClick = () => {
        navigate(userInfo.address ? '/edit-address' : '/add-address');
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    if (!userInfo) {
        return <div className="no-data">No user information available</div>;
    }

    // Lấy danh sách order, nếu không có thì trả về mảng rỗng
    const orderItemList = userInfo.orderItemlist || [];
    const totalPages = Math.ceil(orderItemList.length / itemsPerPage);
    const paginatedOrders = orderItemList.slice(
        (currentPage - 1) * itemsPerPage,
        currentPage * itemsPerPage
    );

    return (
        <div className="profile-page">
            <h2>Welcome {userInfo.name}</h2>

            <div className="user-info">
                <p><strong>Name: </strong>{userInfo.name}</p>
                <p><strong>Email: </strong>{userInfo.email}</p>
                <p><strong>Phone Number: </strong>{userInfo.phoneNumber}</p>
            </div>

            <div className="address-section">
                <h3>Address</h3>
                {userInfo.address ? (
                    <div className="address-info">
                        <p><strong>Street: </strong>{userInfo.address.street}</p>
                        <p><strong>City: </strong>{userInfo.address.city}</p>
                        <p><strong>State: </strong>{userInfo.address.state}</p>
                        <p><strong>Country: </strong>{userInfo.address.country}</p>
                    </div>
                ) : (
                    <p>No address information available</p>
                )}
                <button 
                    className="profile-button" 
                    onClick={handleAddressClick}
                >
                    {userInfo.address ? "Edit Address" : "Add Address"}
                </button>
            </div>

            <div className="order-section">
                <h3>Order History</h3>
                {orderItemList.length > 0 ? (
                    <>
                        <ul className="order-list">
                            {paginatedOrders.map(order => (
                                <li key={order.id} className="order-item">
                                    {order.product && (
                                        <>
                                            <div className="product-info">
                                                <h4>{order.product.name}</h4>
                                                <p><strong>Status: </strong>{order.status}</p>
                                                <p><strong>Quantity: </strong>{order.quantity}</p>
                                                <p><strong>Price: </strong>${order.price.toFixed(2)}</p>
                                            </div>
                                        </>
                                    )}
                                </li>
                            ))}
                        </ul>
                        {totalPages > 1 && (
                            <Pagination
                                currentPage={currentPage}
                                totalPages={totalPages}
                                onPageChange={setCurrentPage}
                            />
                        )}
                    </>
                ) : (
                    <p>No order history available</p>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
