import React, { useState, useEffect } from "react";
import { Navigate, useLocation } from "react-router-dom";
import ApiService from "./ApiService";

export const ProtectedRoute = ({element: Component}) => {
    const location = useLocation();
    const [isChecking, setIsChecking] = useState(true);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const token = localStorage.getItem('token');
                console.log('Checking authentication...');
                console.log('Token exists:', !!token);
                
                if (!token) {
                    throw new Error('No token found');
                }

                // Kiểm tra token có hợp lệ không bằng cách gọi API info
                const userInfo = await ApiService.getLoggedInUserInfo();
                console.log('Auth check successful:', userInfo);
                setIsAuthenticated(true);
            } catch (error) {
                console.error('Auth check failed:', error);
                // Xóa token nếu không hợp lệ
                ApiService.logout();
                setIsAuthenticated(false);
            } finally {
                setIsChecking(false);
            }
        };

        checkAuth();
    }, [location]);

    if (isChecking) {
        return <div>Verifying authentication...</div>;
    }

    if (!isAuthenticated) {
        console.log('Not authenticated, redirecting to login');
        return <Navigate to="/login" replace state={{from: location}}/>;
    }

    return Component;
};

export const AdminRoute = ({element: Component}) => {
    const location = useLocation();
    const [isChecking, setIsChecking] = useState(true);
    const [isAuthorized, setIsAuthorized] = useState(false);

    useEffect(() => {
        const checkAdminAuth = async () => {
            try {
                const token = localStorage.getItem('token');
                const role = localStorage.getItem('role');
                console.log('Checking admin authorization...');
                console.log('Token exists:', !!token);
                console.log('Role:', role);

                if (!token || role !== 'ADMIN') {
                    throw new Error('Not authorized as admin');
                }

                // Verify với backend
                const userInfo = await ApiService.getLoggedInUserInfo();
                if (userInfo.role !== 'ADMIN') {
                    throw new Error('User is not an admin');
                }

                setIsAuthorized(true);
            } catch (error) {
                console.error('Admin auth check failed:', error);
                setIsAuthorized(false);
            } finally {
                setIsChecking(false);
            }
        };

        checkAdminAuth();
    }, [location]);

    if (isChecking) {
        return <div>Verifying authorization...</div>;
    }

    if (!isAuthorized) {
        console.log('Not authorized as admin, redirecting');
        return <Navigate to="/" replace/>;
    }

    return Component;
};
